import React from 'react';

const Accordion = ({ items }) => {
    const renderedItems = items.map((item, index) => {
            return <div key={item.item} className='ml-20 mr-20 mb-1 bg-darker r-5 p-10'>
                        <div className='greet color-yellow active' onClick={() => console.log('Title clicked', index)}>
                            <i className='dropDown icon'></i>
                            {item.item}
                        </div>
                        <div className='white description active'>
                            <p>{item.content}</p>
                        </div>
                </div>
        })
    return <div className='accordion'>
            {renderedItems}
        </div>
};

export default Accordion;
