import React, { useEffect } from 'react';
import axios from 'axios';

const Translate = ({language, text}) => {
    useEffect(() => {
        axios.post('https://translation.googleapis.com/language/translate/v2');
    }, [language, text]);
    return <div />
};

export default Translate;
