//stateful component
import React from 'react';
//import ReactDOM from 'react-dom';

import Greeting from './Greeting.js'
import FormLogin from './Forms/FormLogin.js';


class LoginControl extends React.Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = {isLoggedIn: false};
  }

  handleClick(isLoggedIn, user) {
    this.setState({
      isLoggedIn: isLoggedIn, 
      user: user
    });
  }
  handleChange(event) {
    console.log("handleChange => " + event.target.value)
    this.setState({user: event.target.value});
  }

  render() {
    const isLoggedIn = this.state.isLoggedIn;
    const user = this.state.user;
    /*
    let button;
    if (isLoggedIn) {
      button = <LogoutButton onClick={this.handleLogoutClick} />;
    } else {
      button = <LoginButton onClick={this.handleLoginClick} />;
    }
    */
    return (
      <div className='width-100-percent mt-20 responsiveTopMargin'>
        <Greeting isLoggedIn={isLoggedIn} user={user} /><br/>
        <FormLogin isLoggedIn={isLoggedIn} user={user} handleChange={this.handleChange} handleClick={this.handleClick}/>
        {/*button*/}
      </div>
    );
  }
}

export default LoginControl;
