import React, {useState} from 'react';
import getKey from '../utils/KeyGenerator.js';
import { openweatherAPI_KEY, openweatherAPI_BASE_URL } from '../../apis/config';


const CitySelector = () => {
    //api.openweathermap.org/data/2.5/forecast?q=London,us&appid={APIKEY}
    //Metric
    //api.openweathermap.org/data/2.5/forecast?q=London,us&appid={APIKEY}&units=metric
    //Image Source
    //http://openweathermap.org/img/wn/10d@2x.png
    const key = openweatherAPI_KEY;
    const baseUrl = openweatherAPI_BASE_URL;

    const [city, setCity] = useState('London');
    const [results, setResults] = useState('');
    const urlEnd = `/data/2.5/forecast?q=${city},us&appid=`;

    const displayResults = (results) => {
        const list = results.list.map((item) => {
            const main = item.main;
            const temp = `temp: ${main.temp}°`;
            const feels_like = `feels like: ${main.feels_like}°`;
            const temp_min = main.feels_like;
            const temp_max = main.feels_like;
            const humidity = `humidity: ${main.humidity}°`;
            const weather = item.weather[0];
            const mainDescription = weather.main;
            const description = weather.description;
            const icon = weather.icon;
            const wind = item.wind;
            const windSpeed = () => <div>
                                        <div className='color-yellow bold mb-10'>wind: </div>
                                        <div className='r-5 p-10 bg-dkGreen'>{wind.speed} mph</div>
                                    </div>;
            const windDirection = () => <div>
                                        <div className='color-yellow bold mb-10'>wind direction: </div>
                                        <div className='r-5 p-10 bg-dkGreen'>{wind.deg}</div>
                                    </div>;
            const visibility = `visibility: ${item.visibility}`;
            const time = item.dt_txt;
            const cards = [description, temp, feels_like, humidity, windSpeed(), windDirection(), visibility]

            const card = (matchKind) => <div key={getKey("card")} className="bg-lite mr-5 ml-5 p-10 r-10">
                            {/*this.getMatchIcon(matchKind)*/}
                            <div className="greet">{matchKind}</div>
                        </div>;
            return (
                <div className='width100Percant p-20 m-20 r-10 bg-dkGreen'>
                    <div className='color-yellow bg-green greet r-10 p-20 m-20'>
                        <div className='color-yellow'>{time}</div>
                        <h1 className='white'>{mainDescription}</h1>
                    </div>
                    <div className="width100Percent flexContainer">
                        {cards.map((item) => card(item))}
                    </div>
                </div>
            )
        })
        console.log(`display: ${JSON.stringify(results, null, 2)}`)
        const display = list;
        console.log(`display: ${display}`)
        setResults(display)
    }

    const onSearch = () => {
        fetch(`${baseUrl}${urlEnd}${key}`)
        .then((response) => response.json())
//        .then((result) => console.log(`${JSON.stringify(result,null, 2)}`))
        .then((results) => displayResults(results));
    };
    const onKeyDown = (event) => {
        if (event.keyCode === 13) {
            onSearch();
        }
    };
    return (
        <div>
            <h1 className='navBranding color-yellow'>Search your city</h1>
            <input onKeyDown={onKeyDown} value={city} placeholder="Enter city"
                    onChange={(event) => setCity(event.target.value)} className="greet p-20 r-10 w-200 brdr-green"/>
            <button onClick={onSearch} className="ml-5 greet p-20 r-10 w-200 bg-green brdr-green">
                Check Weather
            </button>
            <h1 className='navBranding color-yellow'>Results:</h1>
            <div className='color-neogreen'>{results}</div>
        </div>
    );
};

export default CitySelector;
