//stateful component
import React from 'react';
import TemperatureInput from './TemperatureInput';
import BoilingVerdict from './BoilingVerdict';

class Calculator extends React.Component {

    constructor(props) {
        super(props);
        this.handleCelsiusChange = this.handleCelsiusChange.bind(this);
        this.handleFahrenheitChange = this.handleFahrenheitChange.bind(this);
        this.state = {temperature: '', scale: 'c'};
    }

    handleCelsiusChange(temperature) {
        this.setState({scale: 'c', temperature});
        console.log(`Calculator => handleCelsiusChange => this.state: ${JSON.stringify(this.state).replace(/{/g,'\n{\n').replace(/,/g,',\n').replace(/}/g,'\n}')}`);
    }

    handleFahrenheitChange(temperature) {
        this.setState({scale: 'f', temperature});
    }

    render() {

        const boilingVerdict = new BoilingVerdict({'celsius': ''});
        const scale = this.state.scale;
        const temperature = this.state.temperature;
        const celsius = scale === 'f' ? boilingVerdict.tryConvert(temperature, boilingVerdict.toCelsius) : temperature;
        const fahrenheit = scale === 'c' ? boilingVerdict.tryConvert(temperature, boilingVerdict.toFahrenheit) : temperature;

        console.log(`Calculator => celsius: ${celsius}`);
        return (
            <div>
                <TemperatureInput
                    scale="c"
                    temperature={celsius}
                    onTemperatureChange={this.handleCelsiusChange} />
                <TemperatureInput
                    scale="f"
                    temperature={fahrenheit}
                    onTemperatureChange={this.handleFahrenheitChange} />
                <BoilingVerdict celsius={parseFloat(celsius)} />
            </div>
        );
    }
}

  export default Calculator;
