import React, { useState, useEffect } from 'react';
import LongPressButton from './LongPressButton.js';
import Selector from '../forms/FunctionalSelector.js';
import getKey from '../utils/KeyGenerator.js';
import ScoreCard from './ScoreCard.js';

const SurfScores = ({ players, time, setTime, heatLength, isWinner }) => {

    const [timerOn, setTimerOn] = useState(false);
    const [timesUp, setTimesUp] = useState(false);

    useEffect(() => {
        let intervalId;
        if (timerOn) {
            intervalId = setInterval(() => {
                setTime((prevTime) => {
                    const newTime = prevTime - 1;
                    if (newTime <= 0) {
                        clearInterval(intervalId);
                        setTimesUp(true);
                        return 0;
                    }
                    return newTime;
                });
            }, 1000);
        }

        return () => clearInterval(intervalId);
    }, [timerOn]);

    useEffect(() => {
        if (timesUp) {
            setTimerOn(false);
            setTime(heatLength);
            console.log(`heatLength: ${heatLength}`)
            setTimesUp(false);
        }
    }, [timesUp]);

    const toggleTimer = () => {
        if (timerOn) {
            setTimerOn(false)
        } else {
            setTimerOn(true)
        }
    }

    const surfClass = (playerIndex) => 'flex3Column p-20 size25 bold' + ((isWinner(playerIndex)) ? ' color-neogreen shakingShaka mt-20' : '');

    const getTimerButtonClasses = () => {
        const timerButtonClasses = timerOn ? 'bg-red m-10 r-10 bold size20 p-20 color-light' : 'bg-green m-10 r-10 bold size20 p-20 color-light';
        return timerButtonClasses;
    }
    const getTimerClasses = () => {
        const timerClasses = (time < 120) ? 'blinking-fade' : '';
        return timerClasses;
    }
    const formatTime = (timeInSeconds) => {
        const hours = Math.floor(timeInSeconds / 3600);
        const minutes = Math.floor((timeInSeconds % 3600) / 60);
        const seconds = timeInSeconds % 60;
        const formattedHours = hours.toString().padStart(2, '0');
        const formattedMinutes = minutes.toString().padStart(2, '0');
        const formattedSeconds = seconds.toString().padStart(2, '0');
        return `${formattedHours}:${formattedMinutes}:${formattedSeconds}`;
    };
    const getSurferIndexWithPriority = (priorityIndex) => {
        console.log(`getSurferIndexWithPriority0 => priorityIndex: ${priorityIndex}`)
        for (let i = 0; i < players.length; i++) {
            console.log(`getSurferIndexWithPriority1 => priorityIndex: ${priorityIndex} surferIndex: ${i} surfer${i}PriorityIndex: ${players[i].surfPriority}`)
            if (Number(players[i].surfPriority) === priorityIndex) {
                console.log(`getSurferIndexWithPriority2 => priorityIndex: ${priorityIndex} surferIndex: ${i}`)
                return i;
            }
        }
        return null;
    }
    const getJerseyColor = (surferIndex) => {
        console.log(`getJerseyColor => surferIndex: ${surferIndex}`)
        if (players[surferIndex].surfJerseyColor === undefined) {
            const newPlayers = [...players];
            newPlayers[surferIndex].surfJerseyColor = surferIndex;
            localStorage.setItem('players', JSON.stringify(newPlayers))
            return 0;
        }
        return players[surferIndex].surfJerseyColor;
    }
    const getPriorityColor = (priorityIndex) => {
        const surferIndex = getSurferIndexWithPriority(priorityIndex);
        const jerseyColorIndex = (surferIndex === null) ? null : getJerseyColor(surferIndex);
        const color = (jerseyColorIndex === null) ? 'white' : jerseyColors[jerseyColorIndex];
        return color;
    }
    const jerseyColors = ['white', 'yellow', 'neogreen', 'black', 'blue', 'orange', 'pink', 'red'];
    const getPriorityItem = (index) => <div className={`flex4Column r-10 m-1 w-75 size40 p-25 bg-${getPriorityColor(index)}`} key={getKey(`priorityFlag`)}>
                                            {index}
                                        </div>
    const getPriorityDisplay = () => {
        return <div className='flexContainer' >
            {(players.length>0) ? getPriorityItem(1) : <div></div>}
            {(players.length>1) ? getPriorityItem(2) : <div></div>}
            {(players.length>2) ? getPriorityItem(3) : <div></div>}
            {(players.length>3) ? getPriorityItem(4) : <div></div>}
        </div>
    }

    return <React.Fragment>
        <div className='flexContainer'>
            <div className={`flex2Column ${getTimerButtonClasses()}`} onClick={() => toggleTimer()}>
                {(timerOn) ? 'STOP' : 'START'}
            </div>
            <div className='flex2Column color-dark text-outline-light'>
                {getPriorityDisplay()}
            </div>
        </div>
        <div className='bg-dkBlue m-10 r-10 bold size20 p-20 color-light0'><span className={getTimerClasses()}>{formatTime(time)}</span></div>
        <div>
            <div className='flexContainer color-yellow bold greet ml-10 mr-10'>
                <div className='ml-10 p-10 w-75 bg-dkGreen m-1 r-5-lft'>Rank</div>
                <div className='flex3Column m-1 p-10 bg-dkGreen m-1'>Athlete</div>
                <div className='flex3Column contentRight mr-10 p-10 bg-dkGreen m-1 r-5-rt'>Total</div>
            </div>
        </div>
    </React.Fragment>

}

export default SurfScores;
