import React from "react";

const initialTodos = [
  {
    id: "t1",
    task: "Code",
    complete: false
  },
  {
    id: "t2",
    task: "Cook",
    complete: false
  }
];
const todoReducer = (state, action) => {
  switch (action.type) {
    case "ADD_TODO":
      return state.map(todo => {
        if (todo.id === action.id) {
            console.log(`1a - todoReducer => ADD_TODO:\ntodo.id: ${todo.id}\naction.id: ${action.id}\ntodo: ${JSON.stringify(todo, 2, null)}`)
          return { ...todo, complete: true };
        } else {
            console.log(`1b - todoReducer => ADD_TODO:\ntodo: ${JSON.stringify(todo, 2, null)}`)
          return todo;
        }
      });
    case "REMOVE_TODO":
      return state.map(todo => {
        if (todo.id === action.id) {
            console.log(`2a - todoReducer  => REMOVE_TODO:\ntodo.id: ${todo.id}\naction.id: ${action.id}\ntodo: ${JSON.stringify(todo, 2, null)}`)
          return { ...todo, complete: false };
        } else {
            console.log(`2b - todoReducer  => REMOVE_TODO:\ntodo: ${JSON.stringify(todo, 2, null)}`)
          return todo;
        }
      });
    default:
      return state;
  }
};
const App = () => {
  const [todos, dispatch] = React.useReducer(todoReducer, initialTodos);

  const handleChange = todo => {
    dispatch({
      type: todo.complete ? "REMOVE_TODO" : "ADD_TODO",
      id: todo.id
    });
  };
  return (
    <ul>
      {todos.map(todo => (
        <li key={todo.id} >
            <label>
                <div className='white b p-10'>
                    <input
                    type="checkbox"
                    checked={todo.complete}
                    onChange={() => handleChange(todo)}
                    />
                    <span className="ml-5">{todo.task}</span>
                </div>
            </label>
        </li>
      ))}
    </ul>
  );
};

export default App;
