import React, { useState, useEffect } from 'react';
import images from './SequenceImages.js';

const PhotoSequence = () => {
    const [index, setIndex] = useState(0);
    const imagesFolder = require.context('../../public/images', true);
    const [url, setUrl] = useState(imagesFolder(`./${images[0]}`));
    let play = true;
    const pause = () => play = !play;
    const getAnImage = () => {
        //let url = `https://lh3.googleusercontent.com/${this.state.images[i].image}`;
        console.log(`play: ${play} index: ${index} images.length: ${images.length}`)
        if (play) {
            setUrl(imagesFolder(`./${images[index]}`));
            const newIndex = ((index+1) === images.length) ? 0 : (index+1);
            setIndex(newIndex);  
        } 
    }
    useEffect(() => {     		
        const timerID = setInterval(
            () => getAnImage(),
            800
        );
        return function cleanUp () {
            clearInterval(timerID);
        }
    });

    return (
        <div onClick={pause()}>
            <img id='photo{index}' className="width-100-percent" src={url} alt={url} />
        </div>
    )
}

export default PhotoSequence;
