import React from 'react';
import Dialog from '../functional/Dialog.js';
import Loader from '../utils/Loader.js';

class Tide extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tide: "low",
            height: "1'"
        }
    }
    componentDidMount() {

        let data;
        const returnJSON = (response) => response.json();
        const returnRejection = (response) => Promise.reject({status: response.status, data});
        const validate = (response) => (response.ok) ? returnJSON(response) : returnRejection(response);
        const uri = 'https://jsongithub.github.io/portfolio/assets/data/gpsData.json';
        fetch(uri)
            .then(response => validate(response))
            .then(data => {
                this.setState({
                    tide: "HIGH",
                    height: "6'"
                })
            })
            .catch(err => console.log(`Something went wrong!\nuri: ${uri} \npath: ${window.location.pathname}\n`, err));
    }
    getCurrentTide = () => <div>Tide: {this.state.tide} Height: {this.state.height}</div>;

    componentWillUnmount() {
        clearInterval(this.timerID);
    }
    percent = 'twentyfivePercent mt--70 mb--70';
    loading = () => <div className={this.percent}>
                <Loader isMotionOn={this.props.isMotionOn}/>
            </div>;
    render() {
        return <div className="color-yellow greet">
                {this.loading()}
                {this.getCurrentTide}
            </div>
    };
}

export default Tide;
