//stateful component
import React from 'react';
//import ReactDOM from 'react-dom';

import Greeting from './Greeting.js'
import FormLogin from './Forms/FormLogin.js';


class LoginControl extends React.Component {
  constructor(props) {
    super(props);
    this.handleLoginClick = this.handleLoginClick.bind(this);
    this.handleLogoutClick = this.handleLogoutClick.bind(this);
    this.state = {isLoggedIn: false};
  }

  handleLoginClick() {
    this.setState({isLoggedIn: true});
  }

  handleLogoutClick() {
    this.setState({isLoggedIn: false});
  }

  render() {
    const isLoggedIn = this.state.isLoggedIn;
    let button;

    if (isLoggedIn) {
      button = <LogoutButton onClick={this.handleLogoutClick} />;
    } else {
      button = <LoginButton onClick={this.handleLoginClick} />;
    }

    return (
      <div className='width-100-percent mt-20 responsiveTopMargin'>
        <Greeting isLoggedIn={isLoggedIn} /><br/>
        <FormLogin isLoggedIn={isLoggedIn} handleLogin={this.handleLoginClick} handleLogout={this.handleLogoutClick}/>
        {/*button*/}
      </div>
    );
  }
}

function LoginButton(props) {
  return (
    <button className="button-red" onClick={props.onClick}>
      Login
    </button>
  );
}

function LogoutButton(props) {
  return (
    <button  className="button-red" onClick={props.onClick}>
      Logout
    </button>
  );
}

export default LoginControl;
