import React from 'react';
class TextColorizer extends React.Component {
    text;
    constructor(text) {
        super();
        this.text = text.text;
        //console.log(`text: ${text.text}`);
    }
    colors = ["color-green", "color-yellow", "color-red"];
    colorIndex = -1;
    getNextColor = (color) => { 
        this.colorIndex = (color === (this.colors.length-1)) ? 0 : (color + 1);
        return this.colors[this.colorIndex];
    };
//    total = (text, character) => text + `<span className="${this.getNextColor(this.colorIndex)}">${character}</span>`;

    colorize = (value, index, array) => {
        //console.log("index: " + index); 
        //console.log("array: " + array); 
        //console.log("value: " + value);
        const character = value;
        const colorizedCharacter = `<span className="${this.getNextColor(this.colorIndex)}">${character}</span>`;
        return colorizedCharacter;
    }
    render() {
        const span = `<CharacterElement className=${this.getNextColor(this.colorIndex)} text={"TEST"} />`;
        const letters = this.text.split('');
        const spans = (text, character) => text + span;
        console.log("total: " + letters.reduce(spans));
        letters.reduce(spans);
        console.log(`letters: ${letters}`);
        //return <div className="bigHeader">{this.text.split('').reduce(spans)}</div>;
        return <div className="bigHeader">{span}</div>;
    }
}

export default TextColorizer;

function CharacterElement(props) {
    return (
        <span className={props.className}>
            {props.text}
        </span>
    );
}
