import React from 'react';
import Loader from './utils/Loader.js';
class SpotPicker extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            tide: props.tide,
            swellDirection: props.swellDirection,
            windDirection: props.windDirection
        };
    }
    componentDidMount() {
        let data;
        const returnJSON = (response) => response.json();
        const returnRejection = (response) => Promise.reject({status: response.status, data});
        const validate = (response) => (response.ok) ? returnJSON(response) : returnRejection(response);
        const uri = 'https://jsongithub.github.io/portfolio/assets/data/gpsData.json';
        fetch(uri)
            .then(response => validate(response))
            .then(data => {
                this.setState({
                    isLoaded: true,
                    locations: data
                })
            })
            .catch(err => console.log(`Something went wrong!\nuri: ${uri} \npath: ${window.location.pathname}\n`, err));
    }

    render() {
        console.log(`Location data: ${JSON.stringify(this.state.locations,null,2)}`)
        return (  
            <div className="color-neogreen">
                HELLO from the spot picker:<br/>
                Wind: {this.props.windDirection}<br/>
                Swell: {this.props.swellDirection}<br/>
                Tide: {this.props.tide}<br/>
            </div>         
        )
    }

}
export default SpotPicker;
