import React from 'react';
import GridImage from './GridImage'
import getKey from './KeyGenerator.js';

class SearchBar extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            term: this.props.term || '',
            menuArray: []
        }
        //this.setIt = this.setIt.bind(this);
    }
    updateArray = (array) => {
        this.menuArray = array;
        console.log(`menuArray: ${JSON.stringify(array, null, 2)}`)
        this.setState({menuArray: array})
    }
    clearMenu = () => (this.state.menuArray.length>0) ? this.setState({menuArray: []}) : null;
    onFormSubmit = event =>  {
        event.preventDefault();
        this.clearMenu();
        console.log(`Search Term: ${this.state.term}`);
        this.props.onSubmit(this.state.term, this.updateArray());
    }
    getImage = (item) => <GridImage key={getKey("thumb")} item={item}></GridImage>
    /*
    componentDidMount() {
        if(window.location.pathname.indexOf("Photos") > -1) {
            this.clearMenu();
            this.props.onSubmit('surfing pipeline', this.updateArray);
        }

    }
    */
    render() {
        return (
            <div className='width-100-percent'>
                <form onSubmit={this.onFormSubmit} className='p-10 r-5 bg-lite ml-20 mr-20'>
                        <label className='color-yellow'>Search: </label>
                        <input
                            className='m-5'
                            type="text"
                            value={this.state.term}
                            onChange={e => this.setState({term: e.target.value})}
                        />
                </form>
                <div className='m-20'>
                    {this.state.menuArray.map((item) => this.getImage(item))}   
                </div> 
            </div>
        )
    }
}

export default SearchBar;
