import React from 'react';
class TextColorizer extends React.Component {
    text;
    constructor(text) {
        super();
        this.text = text.text;
        //console.log(`text: ${text.text}`);
    }
    colors = ["color-green", "color-yellow", "color-red"];
    colorIndex = 0;
    getNextColor = () => { 
        this.colorIndex = ((this.colorIndex) === (this.colors.length-1)) ? 0 : (this.colorIndex + 1);
        return this.colors[this.colorIndex];
    };
//    total = (text, character) => text + `<span className="${this.getNextColor}">${character}</span>`;
//    total = (text, character) => text + `<span className="${this.getNextColor()}">${character}</span>`;
    total = <CharacterElement className={this.getNextColor()} />;
    colorize = (value, index, array) => {
        //console.log("index: " + index); 
        //console.log("array: " + array); 
        //console.log("value: " + value);
        const character = value;
        const colorizedCharacter = `<span className="${this.getNextColor()}">${character}</span>`;
        return colorizedCharacter;
    }
    render() {
        //return <div className="bigHeader">{this.text.split('').reduce(this.total)}</div>;
        return <div className="bigHeader">{total}</div>;
    }
}

export default TextColorizer;

function CharacterElement(props) {
    return (
        <span className={props.className}>
            Login
        </span>
    );
}
