import React from 'react';

const DropDown = ({ options }) => {
    const list = options.map((color) => {
        return (
            <div key={color.value} className={`item button r-5 p-10 mr-20 ml-20 bold mb-1 bg-${color.value}`}>
                {color.label}
            </div>
        )
    })
    return (
        <div className='ui form'>
            <div className='field'>
                <label className='label'>Select a color</label>
            </div>
            { list }
        </div>
    )
}
export default DropDown;
