const items = () => this.state.items;
const selected = (item,groupTitle) => ((item.selections.indexOf(this.log[groupTitle][item.description])) !== -1) ? true : false;
const defaultSelection = (item,groupTitle) => (this.selected(item,groupTitle)) ? (item.selections.indexOf(this.log[groupTitle][item.description])) : 0; 

const radioItems = (item, groupTitle) => {
    return (
        <RadioSelector
            header={groupTitle}
            groupTitle={groupTitle} 
            selected={this.defaultSelection(item,groupTitle)} 
            label={item.description} 
            items={item.selections} 
            onChange={this.handleSelection}
        />
    )
};

const radio = (item, groupTitle) => <div className="r-vw bg-green">
            {this.radioItems(item, groupTitle)}
        </div>;

const selectionInterface = (item, groupTitle) => (item.type === "radio") ? this.radio(item, groupTitle) : selector(item, groupTitle, this.spot, this.defaultSelection, this.handleSelection, this.selected);

const categories = () => {
    this.selectorStatus = [];
    return <div className='description'>{groups(this.items, this.log, this.defaultSelection, this.selectionInterface)}</div>;
}
export default categories;
