import React from 'react';

//import Lister from './functional/Lister.js';
//import Name from './Name.js';
import FormEssay from './Forms/FormEssay.js';
import Reservation from './Forms/FormReservation.js';
import AcaiBowSelector from './Forms/FunctionalFormSelector.js';
import PokiBowSelector from './Forms/FormSelector.js';
import TextColorizer from './TextColorizer.js';
//import Repository from './Repository.js';
import Timer from './Timer.js';
//import Greeting from './Greeting.js';
import Calculator from './Calculator.js';
import SignUpDialog from './SignUpDialog.js';
import Logger from './Logger.js';
import Loader from './Loader.js';
import Footer from './Footer.js';
import Header from './Header.js';

//import logo from './assets/images/logo.svg';
import logoRed from './assets/images/logoRed.png';
import logoYellow from './assets/images/logoYellow.png';
import logoGreen from './assets/images/logoGreen.png';
//import shakaWhite from './assets/images/shakaWhite.png';
import shakaBlack from './assets/images/shakaBlack.png';
import './css/App.css';

//const user = { firstName: 'Furious', lastName: 'Styles'};
const company = "KFA";
const Home = <div className="App">
                <header className="App-content pb-400">
                  <a className="App-link"
                    href="https://jsongithub.github.io/portfolio/"
                    target="_self"
                    rel="noopener noreferrer"
                  >
                    portfolio
                  </a>
                  <br />
                  {/*<Lister items={[1,2,3]} />*/}
                  <Timer/>
                </header>
              </div>

const PokiBowl = <PokiBowSelector 
                    selected={"Ahi"} 
                    header = {"Poki Bowl"} 
                    items = {[
                      "White Rice", 
                      "Spring Mix", 
                      "Ahi", 
                      "Shrimp", 
                      "Octopus", 
                      "Salmon",
                      "Albacore", 
                      "Not spicy", 
                      "Medium Spicy", 
                      "Spicy", 
                      "Eel sauce", 
                      "Miso sauce", 
                      "Wasabi", 
                      "Cucumber",
                      "Onion",
                      "Imitation crab", 
                      "Pineapple", 
                      "Mandorine Orange", 
                      "Raddish",
                      "Edamame", 
                      "Ginger", 
                      "Seaweed Salad", 
                      "Masago", 
                      "Green onion", 
                      "Fried garlic",
                      "Tempura crisp", 
                      "Furikake", 
                      "Dried seaweed", 
                      "Sesame Seeds",
                      "Extra eel",
                      "Exra Miso"
                  ]} />

const AcaiBowl = <AcaiBowSelector 
                  selected={"Mango"} 
                  label={"Acai Bowl"} 
                  items={[
                      "Granola", 
                      "Banana", 
                      "Acai", 
                      "Applesauce", 
                      "Chia Seeds", 
                      "Peanut Butter", 
                      "Yogurt", 
                      "Mango", 
                      "Pineapple", 
                      "Strawberry", 
                      "Coconut"
                    ]}/>

const BowlBuilder = <div>
                      <header />
                      <div className="flexContainer App-content">
                        <div className="flex5Column"></div>
                        <div className="flex5Column">{PokiBowl}</div>
                        <div className="flex5Column"></div>
                        <div className="flex5Column">{AcaiBowl}</div>
                        <div className="flex5Column"></div>
                      </div>
                      <footer />
                    </div>

function App() {
  console.log(`window.location.pathname: ${window.location.pathname}`)
  if (window.location.pathname === "/Home") {
    return (
      <div className="App">
        <Header company={company}/>
        {Home}
        <Footer />
      </div>
    )
  } else if (window.location.pathname === "/BowlBuilder") {
    return (
      <div className="App">
        <Header company={company}/>
        {BowlBuilder}
        <Footer />
      </div>
    )
  } else if (window.location.pathname === "/TempConverter") {
    return (
      <div className="App">
        <Header company={company}/>
        <Calculator />
        <Footer />
      </div>
    )
  } else if (window.location.pathname === "/Essay") {
    return (
      <div className="App">
        <Header company={company}/>
        <FormEssay className='mt-40'/>
        <Footer />
      </div>
    )
  } else if (window.location.pathname === "/Reservation") {
    return (
      <div className="App">
        <Header company={company}/>
        <Reservation />
        <Footer />
      </div>
    )
  } else if (window.location.pathname === "/GuestList") {
    return (
      <div className="App">
        <Header company={company}/>
        <SignUpDialog title="Guest List" message="Sign up"/>
        <Footer />
      </div>
    )
  } else if (window.location.pathname === "/SurfLog") {
    return (
      <div className="App">
        <Header company={company}/>
        <Logger />
        <Footer />
      </div>
    )
  } else {
    return (
      <div className="App">
        <Header company={company}/>
          {/*<Repository name="charliewilco/obsidian" />*/}
          <a className="App-link"
            href="https://jsongithub.github.io/portfolio/"
            target="_self"
            rel="noopener noreferrer"
          >
            portfolio
          </a>
          <br />
          {/*<Lister items={[1,2,3]} />*/}
          <Timer className='mt-40'/>
          <br/>
          <div className="flexContainer">
            <div className="flex5Column"></div>
            <div className="flex5Column">
            {/*POKI BOWL BUILDER*/}
              <PokiBowSelector 
                selected={"Ahi"} 
                header = {"Poki Bowl"} 
                items = {[
                  "White Rice", 
                  "Spring Mix", 
                  "Ahi", 
                  "Shrimp", 
                  "Octopus", 
                  "Salmon",
                  "Albacore", 
                  "Not spicy", 
                  "Medium Spicy", 
                  "Spicy", 
                  "Eel sauce", 
                  "Miso sauce", 
                  "Wasabi", 
                  "Cucumber",
                  "Onion",
                  "Imitation crab", 
                  "Pineapple", 
                  "Mandorine Orange", 
                  "Raddish",
                  "Edamame", 
                  "Ginger", 
                  "Seaweed Salad", 
                  "Masago", 
                  "Green onion", 
                  "Fried garlic",
                  "Tempura crisp", 
                  "Furikake", 
                  "Dried seaweed", 
