import React from 'react';
import Dialog from '../functional/Dialog.js';

class Geolocate extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            longitude: "",
            latitude: ""
        }
    }
    componentDidMount() {
        window.navigator.geolocation.getCurrentPosition(
            //position => console.log(position.coords.longitude),
            position => {
                this.setState({
                    longitude: position.coords.longitude,
                    latitude: position.coords.latitude
                });
            },
            err => console.log(err)
        )
    }

    getLocation = () => `Latitude: ${Number(this.state.latitude).toFixed(2)} Longitude: ${Number(this.state.longitude).toFixed(2)}`;

    latlon = () => this.state.latitude + "," + this.state.longitude;

    imageUrl = () => "https://maps.googleapis.com/maps/api/staticmap?center="+
                 this.latlon()+"&zoom=14&size=400x300&sensor=false&key=AIzaSyCeOrNWv3lqS4nF4h8ohlHgOAygDNCyIUw";

    render() {
        return (
            <div>
                    { this.getLocation() }
                    {/*<br/>
                    <img src={this.imageUrl()} alt="location" />*/}
            </div>
        );
    };
}

export default Geolocate;
