import React from 'react';

//<FormSelector items={["Grapefruit", "Lime", "Coconut", "Mango"]} />

function FormSelector(props) {
    const items = props.items;
    const select = props.select ?? "";
    const tagSelected = (item) => <option selected key={item.toString().toLowerCase()} value={item.toString().toLowerCase()}>{item}</option>;
    const tag = (item) => <option key={item.toString().toLowerCase()} value={item.toString().toLowerCase()}>{item}</option>;
    const getTag = (item) => (item === select) ? tagSelected(item) : tag(item);
    const selectItems = items.map((item) =>
        getTag(item)     
    );
    const handleSubmit = () => console.log("SUBMITTED");
    const handleChange = () => console.log("CHANGED");

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Select:<br/>
                <select value={select} onChange={handleChange}>
                    {selectItems}
                </select>
            </label>
            <br/>
            <input type="submit" value="Submit" />
        </form>
    );
}

export default FormSelector;
