import React from 'react';

//<FormSelector items={["Grapefruit", "Lime", "Coconut", "Mango"]} />

function FormSelector(props) {
    const items = props.items;
    const label = props.label;
    const select = props.selected;
    let bowlIngredients = "";
    let selected = select;
    const tagSelected = (item) => <option selected key={item.toString().toLowerCase()} value={item.toString()}>{item}</option>;
    const tag = (item) => <option key={item.toString().toLowerCase()} value={item.toString()}>{item}</option>;
    const getTag = (item) => (item === select) ? tagSelected(item) : tag(item);
    const selectItems = items.map((item) =>
        getTag(item)     
    );
    const handleSubmit = (event) => { 
        console.log("SUBMITTED => " + selected);
        selected = (bowlIngredients.includes(selected)) ? false : selected.toUpperCase();
        if (selected) {
            bowlIngredients = (bowlIngredients === "") ? selected : (bowlIngredients + ", " + selected);
            document.getElementById('bowl').value = bowlIngredients;
        }
        event.preventDefault();
    }
    const handleChange = (event) => {
        console.log("CHANGED => " + event.target.value);
        selected = event.target.value;
        //this.setState({value: e.target.value});
    }
    const showBowlIngredients = (event) => {
        console.log("Bowl Ingredients => " + bowlIngredients);
        //this.setState({value: e.target.value});
    }

    return (
        <form onSubmit={handleSubmit}>
            <textarea id="bowl" rows="4" cols="75" value={bowlIngredients} onChange={showBowlIngredients} /><br/>
            <label>
                {label}<br/>
                <select onChange={handleChange}>
                    {selectItems}
                </select>
            </label>
            <br/>
            <input type="submit" value="Add item" />
        </form>
    );
}

export default FormSelector;
