import React from 'react';
import Dialog from '../functional/Dialog.js';

class Geolocate extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            long: "",
            lat: ""
        }
    }
    componentDidMount() {
        window.navigator.geolocation.getCurrentPosition(
            //position => console.log(position.coords.longitude),
            position => {
                this.setState({
                    long: position.coords.longitude,
                    lat: position.coords.latitude
                });
            },
            err => console.log(err)
        )
    }

    getLocation = () => `Longitude: ${this.state.long} Latitude: ${this.state.lat}`;

    latlon = () => this.state.lat + "," + this.state.long;

    imageUrl = () => "https://maps.googleapis.com/maps/api/staticmap?center="+
                 this.latlon()+"&zoom=14&size=400x300&sensor=false&key=YOUR_KEY";

    // = "<img src='"+img_url+"'>";

    render() {
        return (
            <div>
                <Dialog title="Location" message="I see you...">
                    { this.getLocation() }
                    <br/>
                    { this.imageUrl() }
                </Dialog>
            </div>
        );
    };
}

export default Geolocate;
