import React, { useState, useEffect } from 'react';
import axios from 'axios';

const WikiSearch = () => {
    const [term, setTerm] = useState('');
    const [results, setResults] = useState([]);
    console.log(results);
    //always invoked on mount
    useEffect(() => {
        const search = async () => {
            const { data } = await axios.get('https://en.wikipedia.org/w/api.php', {
                params: {
                    action: 'query',
                    list: 'search',
                    origin: '*',
                    format: 'json',
                    srsearch: term
                }
            });
            setResults(data);
        };
        if (term) {
            debounce(search, 250);
        }

    }, [term]);

    return (
        <div className="m-20">
            <div className='ui form'>
                <div className='field'>
                    <label><span className='color-yellow greet'>Search: </span></label>
                    <input 
                        value={term} 
                        onChange={e => setTerm(e.target.value)}
                        className='input'
                    />
                </div>
            </div>
        </div>
    )
};

export default WikiSearch;
