import React from 'react';

class SelectorForm extends React.Component {
    /*
    items = [
        "White Rice", 
        "Spring Mix", 
        "Ahi", 
        "Shrimp", 
        "Octopus", 
        "Salmon",
        "Albacore", 
        "Not spicy", 
        "Medium Spicy", 
        "Spicy", 
        "Eel sauce", 
        "Miso sauce", 
        "Wasabi", 
        "Cucumber",
        "Onion",
        "Imitation crab", 
        "Pineapple", 
        "Mandorine Orange", 
        "Raddish",
        "Edamame", 
        "Ginger", 
        "Seaweed Salad", 
        "Masago", 
        "Green onion", 
        "Fried garlic",
        "Tempura crisp", 
        "Furikake", 
        "Dried seaweed", 
        "Sesame Seeds",
        "Extra eel",
        "Exra Miso"
    ];
    */
    header = "Select:";
    bowlIngredients = "";
    selected = this.state.select;
    constructor(props) {
        super(props);
        this.state = {};
        this.state.value = props.selected;
        this.state.select = props.selected;
        this.state.items = props.items;
        this.state.header = props.header;
        //this.select = props.selected;
        //this.items = props.items;
        //this.header = props.header;
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        console.log('You Selected: ' + event.target.value);
        this.selected = event.target.value;
        this.setState({value: event.target.value});
    }
    showBowlIngredients = (event) => {
        console.log("Bowl Ingredients => " + this.bowlIngredients);
        //this.setState({value: e.target.value});
    }
    handleSubmit(event) {
        this.selected = (this.bowlIngredients.includes(this.selected)) ? false : this.selected;
        if (this.selected) {
            this.bowlIngredients = (this.bowlIngredients === "") ? this.selected : (this.bowlIngredients + ", " + this.selected);
            document.getElementById('ingredients').value = this.bowlIngredients;
        }
        //console.log('Items: ' + this.items.length);
        console.log('You Submitted: ' + this.state.value);
        console.log("SUBMITTED => " + this.selected);
        event.preventDefault();
    }
    selectItems = this.state.items.map((item) =>
        <option 
            key={item.toString().toLowerCase()} 
            value={item.toString()}>{item}
        </option>    
    );

    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <textarea id="ingredients" rows="11" cols="10" value={this.bowlIngredients} onChange={this.showBowlIngredients} /><br/>
                <label>
                    {this.header}<br/>
                    <select value={this.state.value} onChange={this.handleChange}>
                        {this.selectItems}
                    </select>
                </label><br/>
                <input type="submit" value="Submit" />
            </form>
        );
    }
}
export default SelectorForm;
