import React from 'react';

class FormLogin extends React.Component {

    constructor(props) {
        super(props);
        this.state = {value: ''};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleLoginClick = this.handleLoginClick.bind(this);
        this.handleLogoutClick = this.handleLogoutClick.bind(this);
        this.state = {isLoggedIn: false};
    }

    handleChange(event) {
        this.setState({value: event.target.value});
        this.setState({isLoggedIn: true});
    }

    handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value + ' => logged in: ' + this.state.isLoggedIn);
        event.preventDefault();
    }

    handleLoginClick() {
        this.setState({isLoggedIn: true});
        this.props.handleClick(true);
    }

    handleLogoutClick() {
        this.setState({isLoggedIn: false});
        this.props.handleClick(false);
    }

    render() {

        const isLoggedIn = this.state.isLoggedIn;

        let button;

        if (isLoggedIn) {
            button = <LogoutButton type="submit" value="Sign in" className="ml-2" onClick={this.handleLogoutClick} />;
        } else {
            button = <LoginButton type="submit" value="Sign out" className="ml-2" onClick={this.handleLogoutClick} />;
        }

        return (
            /*
            <div className='width-100-percent mt-20'>
                <Greeting isLoggedIn={isLoggedIn} /><br/><br/>
                {button}
            </div>
            */
            <form onSubmit={this.handleSubmit}>
                <span>{this.state.value}</span>
                <input type="text" value={this.state.value} onChange={this.handleChange}/>
                {/*<button type="submit" value="Sign in" className="ml-2" onClick={this.handleLoginClick}>Sign in</button>*/}
                {button}
            </form>            
        );
    }
}

export default FormLogin;
