import React, { useState } from 'react';
export default function Todos(props) {

    const [todos, setTodos] = useState(['Learn Hooks', 'Implement hooks']);
//    const [cart, setCart] = useState({id: 'xx22', name: 'iphone 8', quantity: 1});
    const addTodo = () => setTodos(prevTodos => [...prevTodos, document.getElementById("todo").value]);
    const removeTodo = e => {
        const id = e.target.id;
        todos.splice(id,1)
        setTodos([...todos]);
    }
    return (
        <div>
            <div className="color-yellow p-5 r-10 flexContainer">
                <div className="flex3Column"></div>
                <div className="p-5 r-10 flex3Column bg-darker">
                    <label>
                        Add an item: 
                        <input className="m-10" type="text" id="todo" name="todo" />
                        <button className="r-5 greet bold bg-yellow m-5 p-5" onClick={() => addTodo()}>ADD</button>
                    </label>
                </div>
                <div className="flex3Column"></div>
            </div>
            <div className="color-yellow p-5 r-10 flexContainer">
                <div className="flex3Column"></div>
                <div id="list" className="p-5 r-10 flex3Column bg-darker">
                    {todos.map((todo, index) => (
                        <div className="flexContainer p-10 bg-lighter">
                            <div className="flex2Column columnLeft">
                                {todo}
                            </div>
                            <div className="flex2Column10Percent">
                                <button id={index} name={todo} className="r-5 greet bold bg-red m-5 p-5" onClick={(e) => removeTodo(e)}>X</button>
                            </div>
                        </div>
                    ))}
                </div>
                <div className="flex3Column"></div>
            </div>
        </div>
    );
}
