import React from 'react';
/*
<SelectorForm 
          selected={"Ahi"} 
          header = {"Poki Bowl"} 
          items = {["one", "two", "three"]}
        />
*/
class SelectorForm extends React.Component {
    action = "YEWW!!!!";
    constructor(props) {
        super(props); 
        this.items = props.items;
        this.state = { 
            getAction: () => this.action,
            value: props.selected,
            selected: props.selected,
            select: props.selected,
            header: props.header,
            bowlIngredients: "",
            selectItems: this.items.map((item) =>
                <option 
                    key={item.toString().toLowerCase()} 
                    value={item.toString()}>{item}
                </option>    
            )
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleChange(event) {
        console.log('You Selected: ' + event.target.value);
        this.setState({
            selected: event.target.value,
            value: event.target.value
        });
    };
    showBowlIngredients = (event) => {
        console.log("Bowl Ingredients => " + this.state.bowlIngredients);
        //this.setState({value: e.target.value});
    };
    handleSubmit(event) {
        const selectedState = (this.state.bowlIngredients.includes(this.state.selected)) ? false : this.state.selected;
        const emptyBowl = (this.state.bowlIngredients === "") ? true : false;
        const setBowlIngredients = (emptyBowl) ? this.state.selected : (this.state.bowlIngredients + ", " + this.state.selected);
        if (selectedState && this.action === "add") {
            console.log("handleSubmit => action: " + this.action + " selectedState: " + selectedState + " setBowlIngredients: " + setBowlIngredients);
            this.setState({ bowlIngredients: setBowlIngredients });
            document.getElementById('ingredients').value = this.state.bowlIngredients;
        }
        //console.log('Items: ' + this.items.length);
        console.log('You Submitted: ' + this.state.value);
        console.log("SUBMITTED => " + this.state.selected);
        event.preventDefault();
    };
    add = (event) => console.log(this.state.getAction);
    remove(event) { console.log(this.action) };

    render() {
        return (
            <form onSubmit={this.handleSubmit} className="neumorphism p-20">
                {this.state.header}<br/><br/>
                <textarea id="ingredients" rows="11" cols="10" className="mb-20" value={this.bowlIngredients} onChange={this.showBowlIngredients} /><br/>
                <label>
                    <select value={this.state.value} onChange={this.handleChange}>
                        {this.state.selectItems}
                    </select>
                </label><br/>
                <input type="submit" value="Add Item" onClick={this.add()}/>
                <input type="submit" value="Remove Item" onClick={this.remove()} className="ml-2"/>
            </form>
        );
    };
}
export default SelectorForm;
