import React from 'react';
/*
<FormRadio 
          selected={"Ahi"} 
          header = {"Poki Bowl"} 
          items = {["one", "two", "three"]}
        />
*/
class FormRadio extends React.Component {

    constructor(props) {
        super(props); 
        this.items = props.items;
        this.state = { 
            value: props.selected,
            selected: props.selected,
            select: props.selected,
            header: props.header,
            label: props.label,
            groupTitle: props.groupTitle,
            items: props.items,
            selectItems: this.items.map((item) =>
                <div>
                    <input key={item.toString().toLowerCase()} type="radio" value={item.toString()} onChange={this.handleChange} checked={false}/>
                    <span className="description">{item.toString()}</span>
                </div>
            )
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    };

    handleChange(event) {
        alert(event.target.value)
        this.setState({
            selected: event.target.value,
            value: event.target.value
        });
    };
    handleSubmit(event) {
        event.preventDefault();
    };

    render() {
        return (
            <label className="m-5 p-20 m-5">    
                {this.state.selectItems}
            </label>
        );
    };
}
export default FormRadio;
