import React from 'react';
import shakaBlack from '../assets/images/shakaBlack.png';
import thumbsUp from '../assets/images/ThumbsUp.png';
import thumbsDown from '../assets/images/ThumbsDown.png';
/*
<FormRadio 
          selected={"Ahi"} 
          header = {"Poki Bowl"} 
          items = {["one", "two", "three"]}
        />
*/
class FormRadio extends React.Component {

    constructor(props) {
        super(props); 
        this.items = props.items;
        this.state = { 
            value: props.selected,
            selected: props.selected,
            select: props.selected,
            header: props.header,
            label: props.label,
            groupTitle: props.groupTitle,
            items: props.items
        }
        this.handleChange = this.handleChange.bind(this);
    };

    handleChange = (event) => {
        this.props.onChange(this.state.groupTitle, this.state.label, event.target.value);
        this.setState({
            selected: event.target.id,
            value: event.target.value
        });
    }
    selectItems() {
        const getIcon =(item, index, select) => {
            const value = item.toString();
            const displayClass = (select === true) ? "shakingShaka shaka" : "shaka";

            let displayColor = (value === "Firing") ? "bg-neogreen" : "bg-yellow";
            displayColor = (value === "Bad") ? "bg-red" : displayColor;

            const buttonClass = displayColor + " pt-10 pb-5 radius-10 m-5 button";

            const click = () => {
                this.setState({
                    selected: index,
                    value: item
                });
            }

            let icon = (value === "Firing") ? shakaBlack : thumbsUp;
            icon = (value === "Bad") ? thumbsDown : icon;

            return <div className={buttonClass} onClick={click}>
                <img id={value} src={icon} alt={value} className={displayClass} /><br/>
                <div className="p-10">
                    <input id={index} key={item.toString().toLowerCase()+(Math.round(Math.random()*100))} type="radio" value={item.toString()} onChange={click} checked={Number(this.state.selected) === Number(index) ? true : false}/>
                    <span className="description color-black ml-5" key={item.toString().toLowerCase()+(Math.round(Math.random()*100))}>{item.toString()}</span>
                </div>
            </div>;
        }

        const icon = (item, index, select) => {
            let iconOut = "";
            iconOut = getIcon(item, index, select);
            return iconOut;
        } 


        return this.state.items.map((item, index) =>
            <div className="flex3Column" key={item.toString().toLowerCase()+(Math.round(Math.random()*100))} >
                {icon(item, index, (Number(this.state.selected) === Number(index)) ? true : false)}
            </div>
        )

    }

    render() {
        //this.props.onChange(this.state.groupTitle, this.state.label, this.state.value);
        return (
            <div className="m-5 p-20 m-5">    
                {this.selectItems()}
            </div>
        );
    };
}
export default FormRadio;
