import React from 'react';
/*
<SelectorForm 
          selected={"Ahi"} 
          header = {"Poki Bowl"} 
          items = {["one", "two", "three"]}
        />
*/
class SelectorForm extends React.Component {

    constructor(props) {
        super(props);
        this.items = props.items;
        this.state = { 
            value: props.selected,
            selected: props.selected,
            select: props.selected,
            header:props.header,
            bowlIngredients: "",
            selectItems: this.items.map((item) =>
                <option 
                    key={item.toString().toLowerCase()} 
                    value={item.toString()}>{item}
                </option>    
            )
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        console.log('You Selected: ' + event.target.value);
        this.setState({
            selected: event.target.value,
            value: event.target.value
        });
    }
    showBowlIngredients = (event) => {
        console.log("Bowl Ingredients => " + this.state.bowlIngredients);
        //this.setState({value: e.target.value});
    }
    handleSubmit(event) {
        this.selected = (this.bowlIngredients.includes(this.selected)) ? false : this.selected;
        if (this.selected) {
            this.bowlIngredients = (this.bowlIngredients === "") ? this.selected : (this.bowlIngredients + ", " + this.selected);
            document.getElementById('ingredients').value = this.bowlIngredients;
        }
        //console.log('Items: ' + this.items.length);
        console.log('You Submitted: ' + this.state.value);
        console.log("SUBMITTED => " + this.selected);
        event.preventDefault();
    }
