import React from 'react';
import PostDirectory from './PostDirectory.js';

const LogId = ({ logId }) => {

    const postDirectory = new PostDirectory();
    const logID = (logId === null || logId === undefined) ? postDirectory.getLastId() : logId;
    const log = (JSON.parse(localStorage.getItem(logID)) === null) ? logTemplateData : JSON.parse(localStorage.getItem(logID));

    getDate = () => log.Day.Date;
    generateNewLogId = () => {
        const date = new Date()
        const st = date.toDateString().replace(/ /g,"");
        const nd = date.toLocaleTimeString().replace(/ /g,"");
        localStorage.setItem("lastPostId", `${st}${nd}`);
        const newId = `${st}${nd}`;
        console.log(`LogId: generateNewLogId => logID: ${newId}`);
        return newId;
    }
    getLogId = () => logID;

    console.log(`LastLogId => render: this.state.logID: ${logID} - this.state.log: ${log}`);
    return <p>Count: {postDirectory.length}</p> 

}
export default LogId;
