import surfScoring from './SurfScoring.js'


export const getSurfTotal = (player) => {
    let total = 0;
    const score = (index) => player.surfScores[index];
    const addToTotal = (value) => total = total + value;
    surfScoring.map((target, index) => {
            addToTotal(Number(score(index)))
        }
    );
    return total;
}
export const getSurferIndexWithPriority = (players, priorityIndex) => {
    for (let i = 0; i < players.length; i++) {
        if (Number(players[i].surfPriority) === priorityIndex) {
            return i;
        }
    }
    return null;
}
export const losePriority = (players, selectedPriority) => {
    const newPlayers = [...players];
    newPlayers.map((player, index) => {
        if (player.surfPriority == selectedPriority) {
            player.surfPriority = newPlayers.length;
        } else if (player.surfPriority != 1 && selectedPriority != newPlayers.length) {
            const shift = player.surfPriority - 1;
            player.surfPriority = (shift > 0) ? shift : newPlayers.length;
        }
    });
    localStorage.setItem('players', JSON.stringify(newPlayers, null, 2));
    setPlayers(newPlayers);
}
export const shiftPriority = (priorityIndex) => {
    const newPlayers = [...players];
    newPlayers.map((player, index) => {
        if (player.surfPriority === (priorityIndex - 1)) {
            player.surfPriority = priorityIndex;
        } else if (player.surfPriority === priorityIndex) {
            player.surfPriority = (priorityIndex - 1);
        }
    });
    localStorage.setItem('players', JSON.stringify(newPlayers, null, 2));
    setPlayers(newPlayers);
}
export const resetSurfersScores = (playerId) => {
    const newPlayers = [...players];
    newPlayers[playerId].surfScore = 0;
    newPlayers[playerId].surfScores = surfScoring;
    setPlayers(newPlayers);
    getScore();
}
const selectJersey = (playerId, selection) => {
    const newPlayers = [...players];
    newPlayers[playerId].surfJerseyColor = (selection);
    localStorage.setItem('players', JSON.stringify(newPlayers));
    setPlayers(newPlayers);
}
export const getJerseyColorSelector = (playerId, color, index) => <button className={`bg-${color} ht-45 width50px button r-10`} onClick={() => selectJersey(playerId, index)}></button>;
export const editSurfer = (playerId) => <div className='m-10 p-10 r-10 bg-darker'>
        <div className='size30 p-10'>Name:</div>
        <button className={`bg-lite size30 color-lite width-100-percent pb-10 pt-10 button r-10`} onClick={() => editPlayer(playerId)}>{players[playerId].name}</button>
        <div className='size30 p-10'>Jersey color:</div>
        <div>
            {jerseyColors.map((color,index) => 
                index === Math.ceil(jerseyColors.length / 2) ? (
                    <React.Fragment key={index}>
                        <br />
                        {getJerseyColorSelector(playerId, color, index)}
                    </React.Fragment>
                ) : (
                    getJerseyColorSelector(playerId, color, index)
                )
            )}
        </div>
        <div>
            <button className='bg-lite size30 color-lite width-100-percent pb-10 pt-10 button r-10 m-5' onClick={() => resetSurfersScores(playerId)}>
                Reset Scores
            </button>
            <button className='bg-lite size30 color-lite width-100-percent pb-10 pt-10 button r-10 m-5' onClick={() => deleteAthlete(playerId)}>
                Delete Athlete
            </button>
        </div>
    </div>

const scoreTotal = (index, playersArray) => Number(highestScore(index, playersArray) + secondHighestScore(index, playersArray)).toFixed(2);

export const isTieScore = (index) => {
    const score = scoreTotal(index, players);
    if (index !== 0) {
        if (score === scoreTotal(index - 1, players)) {
            return true;
        }
    }
    return false;
}
export const place = (index) => (isTieScore(index) && (index !== 0)) ? index : (index + 1);
export const placeSuffix = (index) => rank[(isTieScore(index) && (index !== 0)) ? (index-1) : (index > 2) ? 3 : (index)];
export const position = (index) => `${place} ${placeSuffix}`;
export const rank = ['st', 'nd', 'rd', 'th'];
export const getTwoHighestScores = (index) => {
    const player = players[index];
    const scores = player.surfScores;
    const [highestIndex, secondHighestIndex] = findTwoHighestIndices(scores);
    //console.log(`getTwoHighestScores => playerIndex: ${index} player: ${JSON.stringify(player,null,2)} name: ${(player.player || player.name)}: Highest score at index ${highestIndex}, Second highest score at index ${secondHighestIndex}`);
    return {
        highScoreIndex: highestIndex,
        secondHighScoreIndex: secondHighestIndex
    };
};
export const highestScore = (index, playersArray) => {
    return playersArray[index].surfScores[getTwoHighestScores(index).highScoreIndex];
}
export const secondHighestScore = (index, playersArray) => playersArray[index].surfScores[getTwoHighestScores(index).secondHighScoreIndex];

export const getScoreTotal = (index) => {
    const highestScore = players[index].surfScores[getTwoHighestScores(index).highScoreIndex]
    const secondHighestScore = players[index].surfScores[getTwoHighestScores(index).secondHighScoreIndex]
    const score = (highestScore + secondHighestScore);
    return score;
}
