import React from 'react';
import ReactDOM from 'react-dom';

class Timer extends React.Component {

    timeItIs = [
        "shaka time!!!",
        "get a wave brah!",
        "keep froth alive...",
    ];
    logos = [1,2,3];
    x = 0;
    interval = 0;
    timeItIsNow = this.timeItIs[this.x];
    logoStacker = (value, index, array) => {
        //console.log("index: " + index); 
        //console.log("array: " + array); 
        //console.log("value: " + value);
        let newId = value+1;
        newId = (newId > array.length) ? 1 : newId;
        let oldId = newId-1;
        oldId = (oldId === 0) ? array.length : oldId;
        console.log(`logo${value} => logo${newId}`)
        document.getElementById('logo'+value).className = "logo" + newId + " absolute logo height200 ml-100";
    }

    constructor(props) {
        super(props);
        this.state = {date: new Date()};
    }

    componentDidMount() {
        this.timerID = setInterval(
            () => this.tick(),
            1000
        );
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }
    tick() {
        this.setState({
            date: new Date()
        });
    }
    render() {
        this.interval = (this.interval === 3) ? 0 : (this.interval+1);
        this.x = (this.x === (this.timeItIs.length-1) && (this.interval === 0)) ? 0 : ((this.interval === 0) ? (this.x+1) : this.x);
        this.timeItIsNow = this.timeItIs[this.x];
        if (this.x === 0) {
            this.logos.forEach(this.logoStacker);
        }
        return (
            <div className="time mt-40">
                <div className='color-red'>
                    do you know what time it is?
                </div>
                <br/>
                <b>
                    {this.state.date.toLocaleTimeString().replace(" ","").toLocaleLowerCase()}
                </b>
                <br/>
                <br/>
                <div className='color-green'>
                    <b>{ this.timeItIsNow }</b>
                </div>
            </div>
        )
    }
}
