import React, { useState, useEffect } from 'react';
import getKey from '../utils/KeyGenerator.js';

const TripFuelTracker = () => {

    /*
    const [location, setLocation] = useState('');
    const [exchangeRate, setExchangeRate] = useState(0);
    const [pricePerLiter, setPricePerLiter] = useState(0);
    const [pricePerLiterUSD, setPricePerLiterUSD] = useState(0);
    const [litersPurchased, setLitersPurchased] = useState(0);
    const [gallons, setGallons] = useState(0);
    const [usdPerGallon, setUsdPerGallon] = useState(0);
    const [totalUSD, setTotalUSD] = useState(0);
    const [totalPesos, setTotalPesos] = useState(0);
    const [trips, setTrips] = useState([]);
    const [trip, setTrip] = useState({});
    */

    const [formData, setFormData] = useState({
        location: '',
        exchangeRate: 0,
        pricePerLiter: 0,
        pricePerLiterUSD: 0,
        litersPurchased: 0,
        gallons: 0,
        usdPerGallon: 0,
        totalUSD: 0,
        totalPesos: 0,
        trips: [],
        trip: {},
    });

    const {
        location,
        exchangeRate,
        pricePerLiter,
        litersPurchased,
        gallons,
        usdPerGallon,
        totalUSD,
        totalPesos,
        trips,
        trip,
    } = formData;

    /*
    useEffect(() => {
        const newTotalUSD = (gallons * usdPerGallon);
        setTotalUSD(newTotalUSD.toFixed(2));
    }, [gallons, usdPerGallon]);
    */
    useEffect(() => {
        const newTotalUSD = (formData.gallons * formData.usdPerGallon);
        setFormData({
            ...formData,
            totalUSD: newTotalUSD.toFixed(2),
        });
    }, [formData.gallons, formData.usdPerGallon]);

    /*
    useEffect(() => {
        if (trips != '[]') {
            alert(JSON.stringify(trips, null, 2));
        }
    }, [trips]);
    */
    useEffect(() => {
        if (formData.trips != '[]') {
            alert(JSON.stringify(formData.trips, null, 2));
        }
    }, [formData.trips]);

    /*
    useEffect(() => {
        if (trip.gallons > 0) {
            const newTrips = [...trips];
            newTrips.push(trip);
            setTrips(newTrips);
        }
    }, [trip]);
    */
    useEffect(() => {
        if (formData.trip.gallons > 0) {
            const newTrips = [...formData.trips];
            newTrips.push(formData.trip);
            setFormData({
                ...formData,
                trips: newTrips,
            })
        }
    }, [formData.trip]);

    const calculate = () => {
        // Convert pricePerLiter from foreign currency to USD
        const pricePerLiterUSD = pricePerLiter / exchangeRate;

        // Calculate gallons
        const newGallons = litersPurchased * 0.264172;
        setGallons(newGallons.toFixed(2));

        // Calculate USD per gallon
        const newUsdPerGallon = pricePerLiterUSD * 3.78541;
        setUsdPerGallon(newUsdPerGallon.toFixed(2));

        const newTrip = {
            location: location,
            gallons: gallons,
            usdPerGallon: usdPerGallon,
            totalUSD: totalUSD
        }
        setTrip(newTrip);

    };

    const handleCalculateAndRecord = () => {
        const newTotalPesos = (litersPurchased * pricePerLiter);
        setTotalPesos(newTotalPesos.toFixed(2));
        calculate();
    };

    return (
        <div>
            <h1 className='color-soft'>Trip Fuel Tracker</h1>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='bg-tinted r-5 w-200 p-10 bold color-soft'>Location:</span>
                </div>
                <div className='p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-200 bg-darker color-soft'
                        type='string'
                        value={location}
                        onChange={(e) => setLocation(e.target.value)}
                    />
                </div>
            </label>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='bg-tinted r-5 w-200 p-10 bold color-soft'>Exchange Rate:</span>
                </div>
                <div className='p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-200 bg-darker color-soft'
                        type='number'
                        value={exchangeRate}
                        onChange={(e) => setExchangeRate(e.target.value)}
                    />
                </div>
            </label>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='bg-tinted r-5 w-200 p-10 bold color-soft'>Pesos per Liter:</span>
                </div>
                <div className='p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-200 bg-darker color-soft'
                        type='number'
                        value={pricePerLiter}
                        onChange={(e) => setPricePerLiter(e.target.value)}
                    />
                </div>
            </label>
            <label className='flexContainer bg-veryLite r-10 m-5'>
                <div className='columnRightAlign width-50-percent'>
                    <span className='bg-tinted r-5 w-200 p-10 bold color-soft'>Total Liters:</span>
                </div>
                <div className='p-10 columnLeftAlign width-50-percent'>
                    <input className='r-5 p-10 w-200 bg-darker color-soft'
                        type='number'
                        value={litersPurchased}
                        onChange={(e) => setLitersPurchased(e.target.value)}
                    />
                </div>
            </label>
            <button className='myButton mt-20' onClick={handleCalculateAndRecord}>Add</button>
            {
                trips.map((trip, index) => <div className='mt-20 color-soft' key={getKey(`trip${index}`)}>
                    <div className='flexContainer bg-tinted r-5 m-5'>
                        <div className='p-5'>
                            <span className='p-5 bold'>{index + 1}</span>
                            <span className='p-5 bold r-5 mt-5 mb-5'>{trip.location}</span>
                            <span className='p-5 bold bg-darker r-5 mt-5 mb-5'>{trip.gallons}</span>
                            <span className='p-5'>gallons</span>
                            <span className='p-5 bold bg-darker r-5 mt-5 mb-5'>${trip.usdPerGallon}</span>
                            <span className='p-5'>per gallon</span>
                            <span className='p-5 bold bg-darker r-5 mt-5 mb-5'>${trip.totalUSD}</span>
                            <span className='p-5'>dollars</span>
                        </div>
                    </div>
                </div>
                )
            }
        </div>
    );
};

export default TripFuelTracker;
