import React, { useState, useEffect } from 'react';
import sequenceImages from './SequenceImages.js';

const PhotoSequence = () => {
    let index = 0;
    const imagesFolder = require.context('../../public/images', true);
    //images: sequenceImages,
    const images = ['IMG_2034.JPG',
        'IMG_2049.JPG',
        'IMG_2060.JPG',
        'IMG_2061.JPG',
        'IMG_2062.JPG',
        'IMG_2063.JPG'
    ];
    //url: `https://lh3.googleusercontent.com/${sequenceImages[0].image}`
    const initialImage = imagesFolder(images[0]);
    console.log(`initialImage: ${initialImage}`)
    const [url, setUrl] = useState(initialImage);
    const getAnImage = () => {
        index = (index === images.length-1) ? 0 : index+1;
        //let url = `https://lh3.googleusercontent.com/${this.state.images[i].image}`;
        console.log(`getAnImage => imgArray[${index}].image: ${images[index]}`)
        setUrl(imagesFolder(images[index]));
    }
    useEffect(() => {     		
        const timerID = setInterval(
            () => getAnImage(),
            1000
        );
        return function cleanUp () {
            clearInterval(timerID);
        }
    });

    return (
        <div>
            <img id='photo{index}' className="width-100-percent" src={url} alt={url} />
        </div>
    )
}

export default PhotoSequence;
