import React from 'react';
//import 'bootstrap/dist/css/bootstrap.min.css';
import { Card } from 'react-bootstrap';
import { openweatherAPI_KEY, openweatherAPI_BASE_URL } from '../../apis/config';
import Dialog from '../functional/Dialog.js';

//api.openweathermap.org/data/2.5/forecast?q=London,us&appid={APIKEY}
//Metric
//api.openweathermap.org/data/2.5/forecast?q=London,us&appid={APIKEY}&units=metric
//Image Source
//http://openweathermap.org/img/wn/10d@2x.png
const key = openweatherAPI_KEY;
const baseUrl = openweatherAPI_BASE_URL;
const city = 'London'
const urlEnd = `/data/2.5/forecast?q=${city},us&appid=`;

const WeatherCard = ({dt, temp_min, temp_max, main, icon}) => {

  const date = new Date(dt);
  const src =`http://openweathermap.org/img/wn/${icon}@2x.png`;

  return (
      <div>
        <div className='bg-dark pb-20 r-5 m-20' style={{width: '18rem'}}>
        <img src={src} alt='weather' />
        <Card.Body>
            <div className='size20 bold mb-20'>{main}</div>
            <p>
            {date.toLocaleDateString()} - {date.toLocaleTimeString()}
            </p>
            <p>Min: {temp_min}</p>
            <p>Max: {temp_max}</p>
        </Card.Body>
        </div>
        <Dialog title={main}
                message={`${date.toLocaleDateString()} - ${date.toLocaleTimeString()}`}>
                <p>Min: {temp_min}</p>
                <p>Max: {temp_max}</p>
        </Dialog> 
    </div>
  );
};

export default WeatherCard;
