import React from 'react';
import Dialog from './functional/Dialog.js';
import shakaBlack from './assets/images/shakaBlack.png';
import thumbsUp from './assets/images/ThumbsUp.png';
import thumbsDown from './assets/images/ThumbsDown.png';

class LogEntry extends React.Component {

    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.handleSignUp = this.handleSubmit.bind(this);
        this.state = {
            title: props.title,
            message: props.message,
            items: props.items,
            buttonLabel: props.buttonLabel
            /*
            selectItems: props.items[0].selections.map((item) =>
                <option 
                    key={item.toString().toLowerCase()} 
                    value={item.toString()}>{item}
                </option>
            )
            */
        };
    }

    handleSelection(value, description) {
        console.log(`Selected: ${value} Description: ${description}`);
    }
    handleChange(e) {
        this.setState({login: e.target.value});
    }
    handleSubmit() {
        alert(`Welcome aboard, ${this.state.login}!`);
    }


    render() {
        const selectId = () => this.id = this.id + 1;
        const items = this.state.items;
        const defaultSelection = (item) => Math.round(item.selections.length/2); 
        const selectionInterface = (item) => (item.type === "radio") ? radio(item) : selector(item);      
        const selectItems = (item) => item.selections.map((item) =>
                <option 
                    key={item.toString().toLowerCase()} 
                    value={item.toString()}>{item}
                </option>
        );
        const radioItems = (item) => item.selections.map((item) =>
            <label className="m-5">
                <input key={item.toString().toLowerCase()} type="radio" value={item.toString()} checked={false} />
                <span className="description">{item.toString()}</span>
            </label>
        );

        const selector = (item) => <fieldset>
            <legend>{item.description}:</legend>
                <select value={item.selections[defaultSelection(item)]} onChange={this.handleSelection(item.type, item.description)}>
                    {selectItems(item)}
                </select>
        </fieldset>;

        const radio = (item) => <fieldset>
            <legend>{item.description}:</legend>
                    {radioItems(item)}
        </fieldset>;

        const fields = () => items.selection.map((item) => selectionInterface(item));
        const group = (item) => item.group;
        const groups = () => items.map((item) => <div className='description m-5'>{item.description}{group(item).map((group) => selectionInterface(group))}</div>)
        const descriptions = () => items.map((group) => <div>{group.description}</div>);
        const categories = () => descriptions().map((category) => <div className='description'>{groups()}</div>);

        const reportAwesome = () => console.log('Firing brah!');
        const reportGood = () => console.log('Fun kine!');
        const reportBad = () => console.log('At least I got wet...');

        return (
            <Dialog title={this.state.title} message={this.state.message}>
                {categories()}
                <br/>
                <textarea value={this.state.entry} onChange={this.handleChange} /><br/><br/>
                <div className="flexContainer">
                    <div className="flex3Column bg-green pt-10 pb-5" onClick={reportAwesome}>
                        <img id="awesome" src={shakaBlack} alt="awesome" className="shaka shakingShaka" />
                    </div>
                    <div className="flex3Column bg-yellow pt-10 pb-5" onClick={reportGood}>
                        <img id="good" src={thumbsUp} alt="good" className="shaka" />
                    </div>
                    <div className="flex3Column bg-red pt-10 pb-5" onClick={reportBad}>
                        <img id="bad" src={thumbsDown} alt="bad" className="shaka" />
                    </div>
                </div>

                <button onClick={this.handleSubmit}>
                    {this.state.buttonLabel}
                </button>
            </Dialog>
        );
    };
}

export default LogEntry;
