import React from 'react';
const fs = require('fs');
const write = require('write');

function FunctionalWrite(filename, data) {

//    console.log(`WRITE ***filename:${filename} data:${JSON.stringify(data)}`)
    (async () => {
        await write.sync({filename}, {data}, { newline: true })
    })();
}

export default FunctionalWrite;
