import React from 'react';

//<FormSelector items={["Grapefruit", "Lime", "Coconut", "Mango"]} />

function FormSelector(props) {
    let action = "";
    const items = props.items;
    const label = props.label;
    const select = props.selected;
    let selected = select;
    const tagSelected = (item) => <option selected key={item.toString().toLowerCase()} value={item.toString()}>{item}</option>;
    const tag = (item) => <option key={item.toString().toLowerCase()} value={item.toString()}>{item}</option>;
    const getTag = (item, index) => (item === select) ? tagSelected(item) : tag(item);
    const selectItems = items.map((item, index) =>
        getTag(item,index)
    );
    const handleSubmit = (event) => { 
        console.log("SUBMITTED => " + selected + " => action: " + action);
        action = "";
        event.preventDefault();
    }
    const handleChange = (event) => {
        console.log(label + " => CHANGED -> " + event.target.value);
        selected = event.target.value;
        //this.setState({value: e.target.value});
    }

    return (
        <form onSubmit={handleSubmit} >
            {label}<br/><br/>
            <label>    
                <select onChange={handleChange}>
                    {selectItems}
                </select>
            </label>
        </form>
    );
}

export default FormSelector;
