import React, { useEffect, useState } from 'react';

const SlideShow = () => {
    let slideShow = true;
    let index = 0;
    const images = [];
    for (let x=1;x<=90;x++) {
        if (x<31) {
            images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/socal/local/socal_large_"+x+".png" });
        } else if (x < 61) {
            images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/nbaja/puntabaja/nearshore/puntabaja_large_"+(x-30)+".png" });
        } else {
            images.push({ "image": "http://slcharts01.cdn-surfline.com/charts/sbaja/local/sbaja_large_"+(x-60)+".png" });
        }
    };
    const [url1, setUrl1] = useState(images[0].image);
    const [url2, setUrl2] = useState(images[30].image);
    const [url3, setUrl3] = useState(images[60].image);

	const getAnImage = () => {
        console.log(`index: ${index} slideShow: ${slideShow}`)
        if (slideShow) {
            index = (index === 29) ? 1 : index+1;
            const i = index;
            console.log(`getAnImage => imgArray[${i}].image: ${images[i].image}`)
            let url1 = images[i].image;
            let url2 = images[i+30].image;
            let url3 = images[i+60].image;
            //console.log(url1);
            //console.log(url2);
            //console.log(url3);
            setUrl1(url1);
            setUrl2(url2);
            setUrl3(url3);
        }
    }
    useEffect(() => {     		
        const timerID = () => setInterval(
            () => getAnImage(),
            700
        ); 	
        timerID();
    });
    /*Hooks method for componentWillUnmount()
    componentWillUnmount() {
        clearInterval(timerID);
    }
    */

    const toggleSlideShow = () => {
        slideShow = !slideShow;
        getAnImage()
    }
    return (
        <div>
            <img id="slideshow1" className="width-100-percent" src={url1} onClick={() => toggleSlideShow()} alt="California Sur Swell" />
            <br/>
            <img id="slideshow1" className="width-100-percent" src={url2} onClick={() => toggleSlideShow()} alt="Baja Norte Swell" />
            <br/>
            <img id="slideshow1" className="width-100-percent" src={url3} onClick={() => toggleSlideShow()} alt="Baja Sur Swell" />
        </div>
    )
}

export default SlideShow;
