import React from 'react';

const Geolocate = () => {
    window.navigator.geolocation.getCurrentPosition(
        position => console.log(position),
        err => console.log(err)
    )
    const showPosition = (position) => latitude(position);
    const getLocation = () => {
        if (navigator.geolocation) {
            //return navigator.geolocation.getCurrentPosition(`latitude: ${latitude} longitude: ${longitude}`);
            navigator.geolocation.getCurrentPosition(showPosition);
        } else {
           return "Geolocation is not supported by this browser.";
        }
    }

    const latitude = (position) => `Latitude: ${position.coords.latitude}`;
    const longitude = (position) => `Longitude: ${position.coords.longitude}`;

    return (
        <div>
            {getLocation()}
        </div>
    )
}

export default Geolocate;
