import React from 'react';
import Dialog from './functional/Dialog.js';
//import shakaBlack from './assets/images/shakaBlack.png';
//import thumbsUp from './assets/images/ThumbsUp.png';
//import thumbsDown from './assets/images/ThumbsDown.png';
import Selector from './Forms/FunctionalSelector.js';
import RadioSelector from './Forms/FormRadio.js';
//import DateTimePicker from 'react-datetime-picker';
import DatePicker from 'react-date-picker';

class LogEntry extends React.Component {

    constructor(props) {
        super(props);
        this.buttonLabel = props.buttonLabe;
        this.title = props.title;
        this.log = {
            Location: {},
            Swell: {},
            Tide: {},
            Wind: {},
            Conditions: {},
        };
        this.message = props.message;
        this.state = {
            date: new Date(),
            items: props.items,
            log: {}
        };
        this.handleSelection = this.handleSelection.bind(this);
        this.updateNotes = this.updateNotes.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.onDateChange = this.onDateChange.bind(this);
    }
    handleSelection(groupTitle, label, selected) {

        if (groupTitle !== 1) {
            let prop = {}
            this.log[groupTitle][label] = selected
            this.props.onChange(groupTitle, label, selected);
            this.setState({
                log: this.log
            });
        } 
    }
    updateNotes(event) {
        this.setState({
            notes: event.target.value
        });
    }
    logIt = () => (this.state.log !== undefined && JSON.stringify(this.state.log) !== "{}") ? console.log(`LogEntry.state.log: ${JSON.stringify(this.state.log)}`) : console.log(`LogEntry.log: ${this.replaceBrackets(this.replaceCommas(this.log)).replace('LogEntry.log: {"Location":{','LogEntry.log: {\n\t\t"Location":{')}`);
    handleSubmit(event) {
        alert(`Your Welcome! Check console...`);
        this.logIt();
        event.preventDefault();
    }
    onDateChange(date) {
        this.setState({
            date: date 
        });
    }
    replaceCommas = (value) => JSON.stringify(value).replace(/,/g,",\n\t");
    replaceBrackets = (value) => (value).replace(/: {"/g,': {\n\t\t"').replace(/:{"/g,':{\n\t\t"').replace(/}}/g,"\n\t}\n}").replace(/},/g,"\n\t},");
    items = () => this.state.items;
    defaultSelection = (item,groupTitle) => ((item.selections.indexOf(this.log[groupTitle][item.description])) !== -1) ? (item.selections.indexOf(this.log[groupTitle][item.description])) : Math.round(item.selections.length/2); 
    radioItems = (item, groupTitle) => <RadioSelector
                    header={groupTitle}
                    groupTitle={groupTitle} 
                    selected={Math.floor(item.selections.length/2)} 
                    label={item.description} 
                    items={item.selections} 
                    onChange={this.handleSelection}
                />;
    selector = (item, groupTitle) => <div className="radius-10 p-20 bg-green">
                <Selector 
                    groupTitle={groupTitle} 
                    selected={this.defaultSelection(item,groupTitle)} 
                    label={item.description} 
                    items={item.selections}
                    onChange={this.handleSelection}
                />
            </div>;

    radio = (item, groupTitle) => <div className="radius-10 bg-green">
                {this.radioItems(item, groupTitle)}
            </div>;
    group = (item) => item.group;
    selectionInterface = (item, groupTitle) => (item.type === "radio") ? this.radio(item, groupTitle) : this.selector(item, groupTitle);

    groups = () => this.items().map((item) => {
        const getKey = () => item.description.toString().toUpperCase()+(Math.round(Math.random()*100));
        const headerClasses = 'subHeader color-yellow';
        const selectorClasses = "greet p-20 bg-vdk-green flex3Column";
        const groupClasses = "flexContainer radius-10";
        const description = item.description;
        return <div key={getKey()}>
                    <div key={getKey()} className={headerClasses}>
                        {item.description}
                    </div>
                    <div className={groupClasses} key={getKey()}>
                        {this.group(item).map((group) => 
                            <div key={getKey()} className={selectorClasses}>
                                {this.selectionInterface(group, description)}
                            </div>
                        )}
                    </div>
                </div>
    })
    categories = () => <div className='description'>{this.groups()}</div>;
    dateEntry = () => <div>
                        <div className='mb-5 subHeader color-yellow'>Date</div>
                        <div className='flexContainer bg-vdk-green'>
                            <DatePicker
                                onChange={this.onDateChange}
                                value={this.state.date} 
                                className='p-10 bg-green flex3Column radius-10 m-20'
                            /><br/>
                        </div>
                    </div>

    render() {
        //this.logIt();
        return (
            <Dialog title={this.title} message={this.message}>
                <form onSubmit={this.handleSubmit}>
                    {this.dateEntry()}
                    {this.categories()}
                    <br/>
                    <textarea value={this.state.entry} onChange={this.updateNotes} /><br/><br/>
                    <button onClick={this.handleSubmit}>
                        {this.buttonLabel}
                    </button>
                </form>
            </Dialog>
        );
    };
}

export default LogEntry;
