import React from 'react';
import ReactDOM from 'react-dom';

class Timer extends React.Component {
    render() {
        return (
            <div className="time mt-40">
                <div className='color-red'>
                    do you know what time it is?
                </div>
                <br/>
                <b>
                    {this.props.date.toLocaleTimeString().replace(" ","").toLocaleLowerCase()}
                </b>
                <br/>
                <br/>
                <div className='color-green'>
                    <b>{ this.props.timeItIsNow }</b>
                </div>
            </div>
        )
    }
}

let timeItIs = [
    "shaka time!!!",
    "get a wave brah!",
    "keep froth alive...",
];
let x = 0;
let interval = 0;
let timeItIsNow = timeItIs[x];
let tick = () => {
    interval = (interval === 3) ? 0 : (interval+1);
    x = (x === (timeItIs.length-1) && (interval === 0)) ? 0 : ((interval === 0) ? (x+1) : x);
    timeItIsNow = timeItIs[x];
    ReactDOM.render(
        <Timer date={ new Date() } timeItIsNow={ timeItIsNow } />,
        document.getElementById('time')
    )
}

setInterval(tick, 1000);
