                return <CricketScore
                    game={game}
                    player={player}
                    index={index}
                    editPlayer={editPlayer}
                    deletePlayer={deletePlayer}
                    updateTwoPlayerScores={updateTwoPlayerScores}
                    winner={winner}
                    key={getKey(player.player)}
                />
            }
            if (dominos) {
                console.log(`${game} - scorecard => player:${player} index: ${index} score: ${player.score}`)
                return <Dominos
                    game={game}
                    player={player}
                    index={index}
                    scoreTotal={player.score}
                    editPlayer={editPlayer}
                    deletePlayer={deletePlayer}
                    getDominoTotal={getDominoTotal}
                    updateTwoPlayerScores={updateTwoPlayerScores}
                    winner={winner}
                    key={getKey(player.player)}
