                player={player}
                index={index}
                editPlayer={editPlayer}
                deletePlayer={deletePlayer}
                updateTwoPlayerScores={updateTwoPlayerScores}
                winner={winner}
                key={getKey(player.player)}
            />
        }
        const rankStatus = (player, index, score) => {
            console.log(`ranksStatus => surfersScores player: ${player.player} index: ${index} score: ${score}`)
            const winsBy = 'Wins by 7.17';
            const needs = 'Needs 0.01';
            if (index === 0) {
                return winsBy
            }
            return needs
        }

        const getWave = (playerId, index, score) => {
            console.log(`getWave => playerId: ${playerId} index: ${index} score: ${score}`);
            const highestScores = findTwoHighestIndices(playerScores[playerId].scores);
            const highestId = highestScores[0];
            const secondHighestId = highestScores[1];
            const firstScore = (index === highestId) ? true : false;
            const secondScore = (index === secondHighestId) ? true : false;
            const getClasses = (firstScore || secondScore) ? 'greet bold color-neogreen p-5' : 'greet color-light p-5';
            const wave = <div className="column">
                <div className='greet bold color-yellow bg-dkYellow w-100 p-5'>{index + 1}</div>
                <div className={getClasses}>{score}</div>
            </div>
            return wave
        }


        const surfingScores = (player, index, score) => {
            if (surf) {
                console.log(`surfingScores => player: ${player.player} index: ${index} score:${playerScores[index].scores[0]}`);
                const surferScores = <div>
                    <div className='flexContainer width-100-percent mt--10 mb--10' key={getKey(player.player)}>
                        <div className='ml-10 p-5 w-100 size40 mt-10'>{index + 1}</div>
                        <div className='flex3ColumnLeft m-1 p-5 size40 mt-10'>{player.player}</div>
                        <div className='flex3ColumnRight mr-10 p-5'>
                            <div className='size25 bold'>{score}</div>
                            <div>{rankStatus(player, index, score)}</div>
                        </div>
                    </div>
                    <div>
                        <div>
                            <div className='h-scroll color-white copyright m-10'>
                                <div class="container mt-10">
                                    {playerScores[index].scores.map((score, wave) => getWave(index, wave, floatDecimalOneDigit(score)))}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                return surferScores
            }
            return
        }
        const renderedItems = players.map((player, index) => scorecard(player, index));

        const dartScores = dartScoring.map((score, index) => <div className='flexContainer width-100-percent' key={getKey(score)}>
            <div className='flex3Column p-5'>
                {scorecard(players[0], index)}
            </div>
            <div className='p-20 r-5 font50 color-yellow m-auto'>
                {score}
            </div>
            <div className='flex3Column p-5'>
                {scorecard(players[1], index)}
            </div>
        </div>);

        const golfScores = golfScoring.map((hole, index) => <div className='flexContainer width-100-percent' key={getKey(hole)}>
